mapper.select:
SELECT p.`key`
FROM permission p
         LEFT JOIN role_permission rp on p.id = rp.pid
         LEFT JOIN user_role ur ON ur.rid = rp.rid AND ur.uid = #{uid}
WHERE p.status = ${@cn.iris.hamster.common.constants.CommonConstants@STATUS_ENABLE}

SELECT count(*)
FROM co_user
WHERE uid = #{uid}

SELECT count(*)
FROM co_user
WHERE uid = #{uid}
  AND cid = #{cid}

SELECT cid
FROM co_user
WHERE uid = #{uid}

SELECT p.id, p.name, p.icon, p.path, p.parent_id
FROM user_role ur
         LEFT JOIN role_permission rp on rp.rid = ur.rid
         LEFT JOIN permission p on p.id = rp.pid
WHERE ur.uid = #{uid}
  AND p.is_menu = ${@cn.iris.hamster.common.constants.CommonConstants@STATUS_ENABLE}
  AND p.status = ${@cn.iris.hamster.common.constants.CommonConstants@STATUS_ENABLE}
ORDER BY p.parent_id

SELECT DISTINCT u.id as id, u.username as username, u.name as `name`, u.gender as gender, u.ID_NO as idNo, u.phone as phone, u.address as address, u.status as `status`, u2.name as create_by, u.create_time as create_time, u3.name as update_by, u.update_time as update_time, IF(r.status = ${@cn.iris.hamster.common.constants.CommonConstants@STATUS_ENABLE}, r.name, '默认用户') as `role` FROM `user` u LEFT JOIN `user` u2 on u.create_by = u2.id LEFT JOIN `user` u3 on u.update_by = u3.id LEFT JOIN user_role ur on u.id = ur.uid LEFT JOIN `role` r on ur.rid = r.id
AND u.`status` = #{query.status}
AND u.`name` LIKE CONCAT('%', #{query.keyword}, '%') OR u.`username` LIKE CONCAT('%', #{query.keyword}, '%')
AND u.gender = #{query.gender}
AND ur.rid = #{query.rid}
ORDER BY u.id LIMIT #{start}, #{query.size}

SELECT COUNT(*) FROM `user` u
LEFT JOIN user_role ur on u.id = ur.uid
LEFT JOIN `role` r on ur.rid = r.id


        AND u.`status` = #{query.status}


        AND u.`name` LIKE CONCAT('%', #{query.keyword}, '%')
        OR u.`username` LIKE CONCAT('%', #{query.keyword}, '%')


        AND u.gender = #{query.gender}


        AND ur.rid = #{query.rid}

SELECT u.id   as id,
       u.name as `name`
FROM `user` u
LEFT JOIN driver_user du on du.uid = u.id
          and du.status = ${@cn.iris.hamster.common.constants.CommonConstants@STATUS_ENABLE}
WHERE u.status = ${@cn.iris.hamster.common.constants.CommonConstants@STATUS_ENABLE}
